import React, { useState, useEffect, Component } from 'react'
import './static/style.css'
import signupImg from './img/signup.jpg'

export default class Login extends Component {
    constructor(props) {
        super(props);
        this.signInBtn = React.createRef();
        this.signUpBtn = React.createRef();
        this.container = React.createRef();
        this.handleSigninClick = this.handleSigninClick.bind(this);
        this.handleSignupClick = this.handleSignupClick.bind(this);
    }

    handleSignupClick = () => {
        const container = this.container.current;
        container.classNameList.add("sign-up-mode");
        console.log("Signin clicked");
    }

    handleSigninClick = () => {
        const container = this.container.current;
        container.classNameList.remove("sign-up-mode");
        console.log("Signup clicked");
    }

    render() {
        return (
            <div>
                <div className="d-flex align-items-center min-vh-100 py-3 py-md-0">
                    <div className="container">
                        <div className="card login-card">
                            <div className="row no-gutters">

                                <div className="col-md-7">
                                    <div className="card-body">
                                        <p className="login-card-description">Create new account</p>

                                        <form action="">
                                            <div className="form-group">
                                                <label for="email" className="sr-only">Email</label>
                                                <input type="email" name="email" id="email" className="form-control" placeholder="Email address"></input>
                                            </div>
                                            <div className="form-group mb-4">
                                                <label for="password" className="sr-only">Password</label>
                                                <input type="password" name="password" id="password" className="form-control" placeholder="************"></input>
                                            </div>
                                            <input name="login" id="login" className="btn btn-block login-btn mb-4 hvr-grow" type="button" value="Login"></input>
                                        </form>
                                        <a href="#" className="forgot-password-link">Forgot password?</a>
                                        <p className="or-social-platforms"> Or use social platforms</p>
                                        <button className="btn btn-block mb-4 google-sign-in hvr-grow">
                                            <p className="btn darken-4 white black-text" href="#">
                                                <div className="left">
                                                    <img width="20px" alt="Google sign-in" 
                                                        src="https://upload.wikimedia.org/wikipedia/commons/thumb/5/53/Google_%22G%22_Logo.svg/512px-Google_%22G%22_Logo.svg.png" />
                                                </div>
                                                <p>Sign-in with Google</p>
                                            </p>
                                        </button>
                                        <p className="login-card-footer-text">Don't have an account?&nbsp; <a href="#" className="register-text">Register here</a></p> 
                                    </div>
                                </div>
                                <div className="col-md-5">
                                    <img src={signupImg} alt="login" className="login-card-img"></img>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
